import { Plateau } from "./composants/Plateau"
import React, { useEffect, useState } from "react";


function App()
{
    //Les états
    const [plateau, setPlateau] = useState(new Plateau);


    //Les comportements

    const handleShuffle = () => {
        if(plateau)
        {
            plateau.melangerCartes();
            console.log(plateau.cartes)
        }
    }

    //Le rendu
    return (
        <div>
            <button onClick={useEffect(handleShuffle)}>Mélanger les cartes</button>
        </div>
    )
}


export default App;
