import Carte from "./Carte"

class Plateau 
{

    constructor()
    {
        this.cartes = this.initCartes();

        this.melangerCartes();
    }

    /*Méthode permettant d'initialiser les cartes du deck*/
    initCartes()
    {
        let tempTabCartes = [];
        let nbCartes = 0;


        for(let j = 1 ; j < 14 ; j++)
        {
            let nombre;

            switch(j)
            {
                case 1      : nombre = "As";    break
                case 11     : nombre = "Valet"; break;
                case 12     : nombre = "Reine"; break;
                case 13     : nombre = "Roi";   break;
                default     : nombre = j;       break;

            }

            tempTabCartes.push(new Carte(nombre, "Coeur",   false));
            tempTabCartes.push(new Carte(nombre, "Trefle",  false));
            tempTabCartes.push(new Carte(nombre, "Pique",   false));
            tempTabCartes.push(new Carte(nombre, "Carreau", false));
        }

        return tempTabCartes;
    }

    /*Méthode permettant de mélanger les cartes du deck*/
    melangerCartes()
    {
        for (let i = this.cartes.length - 1; i > 0; i--) {
            // Générer un index aléatoire entre 0 et i
            const j = Math.floor(Math.random() * (i + 1));
    
            // Échanger les éléments tableau[i] et tableau[j]
            [this.cartes[i], this.cartes[j]] = [this.cartes[j], this.cartes[i]];
        }

        console.log(this.cartes);
    }

}

export { Plateau };
