import { Plateau } from "./composants/Plateau"
import React, { useEffect, useState } from "react";
import "./styles/App.css";


/*Ce composant contient tous les autre composants dans app et permet de dispatcher correcter les différents éléments*/
function PilesVictoire()
{
    return(
        <div id="PilesDiv">
            <canvas class="Piles" width="510" height="250"/>
        </div>
    )
}



function Colonnes()
{
    return (
        <div id ="ColonnesDiv">
            <canvas class="Piles" width="840" height="250"/>
        </div>
    )
}



function App()
{
    //Les états
    const [plateau,  setPlateau ] = useState(null);
    const [jeuLance, setJeuLance] = useState(false);



    //Les comportements
    useEffect(() => {
        const nouveauPlateau = new Plateau()
        setPlateau(nouveauPlateau);
    }, []);

    const handleGameStart = () => {

        if(!jeuLance)
        {
            handleMelangerCartes();
            handleDessinerCartes();
            setJeuLance(true);
        }
    }

    const handleMelangerCartes = () => {
        plateau.melangerCartes();
        plateau.tabFin[0][0] = plateau.cartes.pop();
        plateau.tabFin[1][0] = plateau.cartes.pop();
        plateau.tabFin[2][0] = plateau.cartes.pop();
        plateau.tabFin[3][0] = plateau.cartes.pop();

        console.log(plateau.cartes);
        console.log(plateau.tabColonnes);
    }

    const handleDessinerCartes = () => {

        //Initialisation du canva
        let canva = document.getElementById("Piles");
        let ctx = canva.getContext("2d");
        ctx.font = '25px Arial';

        //images
        const imgCarte   = new Image();
        imgCarte.src    = '/images/Carte.png';


        imgCarte.onload = () => {
            for(let i = 0 ; i < 4 ; i++)
            {
                if(plateau.tabFin[i][0] != undefined)
                {
                    //Les images
                    ctx.drawImage(imgCarte, (i*120) + (i*10), 0, 120, 200);
                    
                    ctx.drawImage(plateau.tabFin[i][0].imgSymbole,(i*130) + 18, 75, 92, 49);
                

                    //Les chiffres
                    ctx.fillStyle = plateau.tabFin[i][0].couleur;
                    ctx.fillText(plateau.tabFin[i][0].getNom(), (i*130) + 5, 25);

                    if(plateau.tabFin[i][0].getNombre() !== 10)
                    {
                        ctx.fillText(plateau.tabFin[i][0].getNom(), (i*130) + 100, 195);
                    }else{
                        ctx.fillText(plateau.tabFin[i][0].getNom(), (i*130) + 90, 195);
                    }
                }
            }
        };
    }

    //Le rendu
    return (
        <div class="BackGround">
            <PilesVictoire></PilesVictoire>
            <Colonnes></Colonnes>
            <button onClick={handleGameStart}>Lancer le jeu</button>

        </div>
    )
}


export default App;
