    const [plateau, setPlateau] = useState(null);
    const [pilesFinales, setPilesFinales] = useState([[],[],[],[]]); //Ceci contient la pile où seront placés les cartes pour la victoire


    //Les comportements
    useEffect(() => {
        const nouveauPlateau = new Plateau()
        setPlateau(nouveauPlateau);
    }, []);

    const handleShuffle = () => {
        if(plateau)
        {
            plateau.melangerCartes();
            console.log(plateau.cartes)
        }
    }

    //Le rendu
    return (
        <div>
            <button onClick={handleShuffle}>Mélanger les cartes</button>
        </div>
    )
}


export default App;
