import { Plateau } from "./composants/Plateau"
import React, { useEffect, useState } from "react";
import "./styles/App.css";


/*Ce composant contient tous les autre composants dans app et permet de dispatcher correcter les différents éléments*/
function PilesVictoire()
{
    return(
        <div id="PilesDiv">
            <canvas id="Piles" class="Piles" width="510" height="200"/>
        </div>
    )
}



function Colonnes()
{
    return (
        <div id ="ColonnesDiv">
            <canvas class="Piles" id="Colonnes" width="900" height="270"/>
        </div>
    )
}

function Pioche()
{
    return (
        <div id="PiocheDiv">
            <canvas class="Piles" id="Pioche" width="250" height="200"/>
        </div>
    )
}



function App()
{
    //Les états
    const [plateau,          setPlateau ]         = useState(null);
    const [jeuLance,         setJeuLance]         = useState(false);


    const [carteSelectionne,    setCarteSelectionne]    = useState(null);
    const [carteColonnesSelect, setCarteColonnesSelect] = useState(null);
    const [cartePilesSelect,    setCartePilesSelect]    = useState(null);

    const [cartePiocheEstSelectionne, setCartePiocheEstSelectionnee] = useState(null);


    //Les comportements
    useEffect(() => {
        const nouveauPlateau = new Plateau();
        setPlateau(nouveauPlateau);
    }, []);


    useEffect(() => {

        if (carteColonnesSelect) 
        {
            handleRechargerPage();
        }
    }, [carteColonnesSelect]);


    useEffect(() => {
        if (carteSelectionne) 
        {
            handleRechargerPage();
        }
    }, [carteSelectionne]);


    useEffect(() => {
        if(plateau !== undefined)
        {
            handleRechargerPage();
        }
    }, [cartePiocheEstSelectionne, plateau])


    //Gestion des clics
    useEffect(() => {

        if (!plateau) return;

        const pilesCanvas = document.getElementById("Piles");
        const colonnesCanvas = document.getElementById("Colonnes");
        const piocheCanvas = document.getElementById("Pioche");



        const handlePilesClick = (event) => {
            const rect = pilesCanvas.getBoundingClientRect();
            const x = event.clientX - rect.left;
            const y = event.clientY - rect.top;
            console.log(`Clic sur Piles : X=${x}, Y=${y}`);
        };



        const handleColonnesClick = (event) => {
            const rect = colonnesCanvas.getBoundingClientRect();
            const x = event.clientX - rect.left;
            const y = event.clientY - rect.top;

            let carteSelect = plateau.tabColonnes[Math.floor(x/130)][plateau.tabColonnes[Math.floor(x/130)].length-1];

            if(carteColonnesSelect === carteSelect)

            setCarteColonnesSelect(plateau.tabColonnes[Math.floor(x/130)][plateau.tabColonnes[Math.floor(x/130)].length-1]);
        };



        const handlePiocheClick = (event) => {
            const rect = piocheCanvas.getBoundingClientRect();
            const x = event.clientX - rect.left;

            if(x > 130)
            {

                if(carteSelectionne !== null)
                {
                    plateau.cartes.unshift(carteSelectionne);
                }
                setCarteSelectionne(plateau.cartes.pop());
            }


            if(x < 120)
            {
                if(cartePiocheEstSelectionne)
                {
                    setCartePiocheEstSelectionnee(false);
                }else
                {
                    setCartePiocheEstSelectionnee(true);
                }
            }
        };


        pilesCanvas.addEventListener("click", handlePilesClick);
        colonnesCanvas.addEventListener("click", handleColonnesClick);
        piocheCanvas.addEventListener("click", handlePiocheClick);

        return () => {
            pilesCanvas.removeEventListener("click", handlePilesClick);
            colonnesCanvas.removeEventListener("click", handleColonnesClick);
            piocheCanvas.removeEventListener("click", handlePiocheClick);
        };
    }, [plateau, carteSelectionne, cartePiocheEstSelectionne]);


    const handleGameStart = () => {

        if(!jeuLance)
        {
            handleMelangerCartes();
            handleRechargerPage();
            setJeuLance(true);
        }
    }

    const handleRechargerPage = () => {
        handleDessinerCartesPilesFin();
        handleDessinerCarteColonnes();
        handleDessinerCartesPioche();
    }


    const handleMelangerCartes = () => {
        plateau.melangerCartes();
    }

    const handleDessinerCartesPilesFin = () => {

        //Initialisation du canva
        let canva = document.getElementById("Piles");
        let ctx = canva.getContext("2d");
        ctx.font = '25px Arial';

        //images
        const imgCarte = new Image();
        imgCarte.src   = '/images/Carte.png';

        const imgDosCarte = new Image();
        imgDosCarte.src   = '/images/dosCarte.jpg';


        imgCarte.onload = () => {
            imgDosCarte.onload = () => {
                for(let i = 0 ; i < 4 ; i++)
                {
                    if(plateau.tabFin[i][0] !== undefined)
                    {
                        if(plateau.tabFin[i][0].getEstRetournee())
                        {
                            ctx.drawImage(imgCarte, (i*120) + (i*10), 0, 120, 200);
    
    
                            ctx.drawImage(plateau.tabFin[i][0].imgSymbole,(i*130) + 18, 75, 92, 49);
    
                            ctx.fillStyle = plateau.tabFin[i][0].couleur;
                            ctx.fillText(plateau.tabFin[i][0].getNom(), (i*130) + 5, 25);
        
                            if(plateau.tabFin[i][0].getNombre() !== 10)
                            {
                                ctx.fillText(plateau.tabFin[i][0].getNom(), (i*130) + 100, 195);
                            }else{
                                ctx.fillText(plateau.tabFin[i][0].getNom(), (i*130) + 90, 195);
                            }
                        }else
                        {
                            ctx.drawImage(imgDosCarte, (i*120) + (i*10), 0, 120, 200);
                        }

                    }else{
                        ctx.drawImage(imgCarte, (i*120) + (i*10), 0, 120, 200);
                    }
                }
            }
        };
    }

    const handleDessinerCarteColonnes = () => {
        //Initialisation du canva
        let canva = document.getElementById("Colonnes");
        let ctx = canva.getContext("2d");
        ctx.font = '25px Arial';

        //images
        const imgCarte = new Image();
        imgCarte.src   = '/images/Carte.png';

        const imgDosCarte = new Image();
        imgDosCarte.src   = '/images/dosCarte.jpg';

        const imgCarteSelect = new Image();
        imgCarteSelect.src   = '/images/CarteSelection.png'


        imgCarte.onload = () => {
            imgDosCarte.onload = () => {

                for(let i = 0 ; i < 7 ; i++)
                {
                    for(let j = 0 ; j < plateau.tabColonnes[i].length ; j++)
                    {

                        if(plateau.tabColonnes[i][j] != undefined)
                        {
                            if(plateau.tabColonnes[i][j].getEstRetournee())
                            {
                                if(plateau.tabColonnes[i][j] === carteColonnesSelect)
                                {
                                    ctx.drawImage(imgCarteSelect, (i*120) + (i*10), j*10, 120, 200);
                                }else
                                {
                                    ctx.drawImage(imgCarte, (i*120) + (i*10), j*10, 120, 200);
                                }
                                
                                ctx.drawImage(plateau.tabColonnes[i][0].imgSymbole,(i*130) + 18, 75 + (j*10), 92, 49);
                            
            
                                if(plateau.tabColonnes[i][0] !== undefined)
                                {
                                    ctx.fillStyle = plateau.tabColonnes[i][0].couleur;
                                    ctx.fillText(plateau.tabColonnes[i][0].getNom(), (i*130) + 5, 25 + (j*10));
                
                                    if(plateau.tabColonnes[i][0].getNombre() !== 10)
                                    {
                                        ctx.fillText(plateau.tabColonnes[i][0].getNom(), (i*130) + 100, 195 + (j*10));
                                    }else{
                                        ctx.fillText(plateau.tabColonnes[i][0].getNom(), (i*130) + 90, 195 + (j*10));
                                    }
                                }
                            }else
                            {
                                ctx.drawImage(imgDosCarte, (i*120) + (i*10), 0  + (j*10), 120, 200);
                            }
                        }
                    }
                };
            }
        }
    }


    const handleDessinerCartesPioche = () => {
        //Initialisation du canva
        let canva = document.getElementById("Pioche");
        let ctx = canva.getContext("2d");
        ctx.font = '25px Arial';

        //images
        const imgCarte = new Image();
        imgCarte.src   = '/images/Carte.png';

        const imgDosCarte = new Image();
        imgDosCarte.src   = '/images/dosCarte.jpg';

        const imgCarteSelect = new Image();
        imgCarteSelect.src = '/images/CarteSelection.png';


        imgCarte.onload = () => {
            imgDosCarte.onload = () => {

                if(carteSelectionne !== null)
                {
                    console.log(cartePiocheEstSelectionne)

                    if(cartePiocheEstSelectionne)
                    {
                        ctx.drawImage(imgCarteSelect, 0, 0, 120, 200);
                    }else
                    {
                        ctx.drawImage(imgCarte, 0, 0, 120, 200);
                    }

                    ctx.drawImage(carteSelectionne.imgSymbole, 18, 75, 92, 49);
                    ctx.fillStyle = carteSelectionne.couleur;
                    ctx.fillText(carteSelectionne.getNom(), 5, 25);


                    if(carteSelectionne.getNombre() !== 10)
                    {
                        ctx.fillText(carteSelectionne.getNom(), 100, 195);
                    }else{
                        ctx.fillText(carteSelectionne.getNom(), 90, 195);
                    }
                }
                
                if(plateau.cartes[plateau.cartes.length-1].getEstRetournee())
                {
                    ctx.drawImage(imgCarte, 130, 0, 120, 200);
                    ctx.drawImage(plateau.cartes[plateau.cartes.length-1].imgSymbole, 148, 75, 92, 49);
                    ctx.fillStyle = plateau.cartes[plateau.cartes.length-1].couleur;
                    ctx.fillText(plateau.cartes[plateau.cartes.length-1].getNom(), 135, 25);
    
    
                    if(plateau.cartes[plateau.cartes.length-1].getNombre() !== 10)
                    {
                        ctx.fillText(plateau.cartes[plateau.cartes.length-1].getNom(), 150, 195);
                    }else{
                        ctx.fillText(plateau.cartes[plateau.cartes.length-1].getNom(), 210, 195);
                    }

                }else
                {
                    ctx.drawImage(imgDosCarte, 130, 0, 120, 200);
                }
            }
        }
    }
    
    
    //Le rendu
    return (
        <div class="BackGround">
            <PilesVictoire></PilesVictoire>
            <Colonnes></Colonnes>
            <Pioche></Pioche>
            <button onClick={handleGameStart}>Lancer le jeu</button>

        </div>
    )
}


export default App;
