import React, {Component} from 'react';

class Carte{

    constructor(nom, nombre, forme, estRetournee)
    {
        this.nom            = nom;
        this.nombre         = nombre;
        this.forme          = forme;
        this.estRetournee   = estRetournee;
        
        if(this.forme === "Trefle" || this.forme === "Pique")
        {
            this.couleur = "black";
        }else{
            this.couleur = "red";
        }

        this.imgCarte       = new Image();

        switch(this.forme)
        {
            case "Trefle" : this.couleur = "black"; this.imgCarte.src = '/images/Trefle.png'; break;
            case "Pique"  : this.couleur = "black"; this.imgCarte.src = '/images/Pique.png'; break;
            case "Coeur"  : this.couleur = "red"  ; this.imgCarte.src = '/images/Coeur.png'
        }

    }

    getNom()
    {
        return this.nom;
    }

    getForme()
    {
        return this.forme;
    }

    getNombre()
    {
        return this.nombre;
    }

    getEstRetournee()
    {
        return this.estRetournee;
    }

    setEstRetournee(bool)
    {
        this.estRetournee = bool;
    }

}

export default Carte
